<?php/* * Post format Image *  */?><?phpif (is_singular()) {    ?>    <div class="post-media">        <?php        $galleries = get_post_meta(get_the_ID(), "post-gallery", true);        $galleries = explode(',', $galleries);        if (!empty($galleries)) {            ?>            <div class="res-video-w">                <div id="page_slider">                    <ul class="slides">                        <?php                        foreach ($galleries as $gallery):                            $img = wp_get_attachment_image((int) $gallery, 'full');                            ?>                            <li><?php echo $img ?></li>                        <?php endforeach; ?>                    </ul>                </div>            </div>            <?php        }        ?>    </div><?php }else { ?>    <div class="post-media">        <?php        $galleries = get_post_meta(get_the_ID(), "post-gallery", true);        $galleries = explode(',', $galleries);        if (!empty($galleries)) {            ?>            <div class="res-video-w">                <div class="page_slider" id="page_slider_<?php echo get_the_ID(); ?>">                    <ul class="slides">                        <?php                        foreach ($galleries as $gallery):                            $img = wp_get_attachment_image((int) $gallery, 'full');                            ?>                            <li><?php echo $img ?></li>                        <?php endforeach; ?>                    </ul>                </div>            </div>            <script type="text/javascript">                jQuery(function($) {                                        $('#page_slider_<?php echo get_the_ID(); ?>').flexslider({                        animation: "fade",                        controlNav: true,                        directionNav: false                    });                });            </script>            <?php        }        ?>    </div>    <h2>        <a href="<?php the_permalink() ?>">            <?php the_title(); ?>        </a>    </h2>    <hr>    <?php}?>