<?phpget_header('alt');global $smof_data;$pagetitle = 'Blog';$postspage = get_option('page_for_posts');if (!empty($postspage) or $postspage = (int) $postspage != 0) {    $blogpage = get_page($postspage);    $pagetitle = $blogpage->post_title;    if (has_post_thumbnail($blogpage->ID)) {        $attach_id = get_post_thumbnail_id($blogpage->ID);        $bgimg = wp_get_attachment_image_src($attach_id, 'full');    }}?><div id="banner_blog" class="parallax_bg"<?phpif (isset($attach_id) && !empty($attach_id)) {    echo " style='background-image:url({$bgimg[0]});'";}?>>    <div class="big_title border"><h1><?php echo $pagetitle ?></h1></div>        </div><div id="content_wrapper">    <section id="blog">        <div class="container">            <div class="nine columns alpha">                <?php if (have_posts()):while (have_posts()):the_post(); ?>                        <article <?php post_class()?>>                            <h2>                                <a href="<?php the_permalink() ?>">                                    <?php the_title(); ?>                                </a>                            </h2>                            <div class="blogmetas">                                <ul>                                    <li><a href="<?php echo get_the_author_meta('user_url') ?>">                                            <img alt="" class="avatar" src="<?php echo get_template_directory_uri() ?>/images/blog_author.png">                                            <span><?php                                            $first_name = get_the_author_meta('first_name');                                            if (!empty($first_name))                                                echo $first_name;                                            else                                                echo get_the_author_meta('user_login');                                            ?></span></a></li>                                    <li><img alt="post-date" src="<?php echo get_template_directory_uri() ?>/images/blog_date.png"> <span><?php echo get_the_date(get_option('date_format')) ?></span></li>                                                                        <?php if(has_tag()){?>                                    <li><i class="fa fa-tags"></i><?php the_tags('',', ','')?></li>                                    <?php }?>                                    <li><img alt="" src="<?php echo get_template_directory_uri()?>/images/blog_comments.png"> <?php                comments_popup_link(__('0', 'szia'), __('1', 'szia'), __('%', 'szia'), '', __('Comments off', 'szia'));                ?></li>                                </ul>                            </div>                            <hr>                            <?php get_template_part('content',  get_post_format());?>                            <div class="post_content">                            <?php the_content();?>                            </div>                            <?php                                wp_link_pages(array('before' => '<div class="post_paginate">' . __('Pages:&nbsp;', 'szia'), 'after' => '</div>', 'next_or_number' => 'number', 'nextpagelink' => '<span class="next">Next &raquo;</span>', 'previouspagelink' => '', 'link_before' => '<span class="button">', 'link_after' => '</span>'));                            ?>                            <?php if(has_category()){?>                            <p class="meta-category">                                <?php _e('Posted in','szia')?> <?php the_category(', ')?>                                                                </p>                            <?php }?>                        </article>                        <?php                    endwhile;                    ?>                    <div class="posts_nav">                                                <?php previous_post_link('%link', _x('%title', 'Previous post link', 'szia')); ?>                        <?php next_post_link('%link', _x('%title', 'Next post link', 'szia')); ?>                                            </div>                       <?php                endif;                ?>                <?php comments_template(); ?>                            </div>            <div class="three columns omega">                <aside id="sidebar">                    <?php                    if(is_active_sidebar('blog')){                        dynamic_sidebar('blog');                    }                    ?>                </aside>            </div>        </div>    </section></div><?phpget_footer();?>