<?php/* * Template Name: Frontpage 1 *  */global $smof_data;get_header();?><div id="content_wrapper">    <?php    $homeslug = 'home';    $frontpage = get_option('page_on_front');    if(!empty($frontpage)){        $home = get_page((int)$frontpage);                if(isset($home->post_name))            $homeslug = $home->post_name;    }    ?>    <div id="<?php echo $homeslug ?>">        <?php        if(isset($smof_data['szia_home_flex_slider']) &&                 !empty($smof_data['szia_home_flex_slider'])):        ?>         <div id="bigslider" class="flexslider">            <ul class="slides">                                <?php foreach ($smof_data['szia_home_flex_slider'] as $slide):                                        ?>                <li><img alt="" src="<?php echo $slide['url']?>" />                    <div class="caption_container"><?php$page = get_posts( array( 'name' => 'we-build-best' ) );if ( $page ){    echo $page[0]->post_content;}?>	                        <div class="flex-caption">                            <h2><?php echo $slide['title']?></h2>                            <h5><?php echo $slide['description']?></h5>                        </div>                    </div>                </li>                <?php endforeach;?>                            </ul>        </div>        <?php        endif;        ?>    </div>	    <?php    $primary_menu_items = szia_nav_menu_items();    if (!empty($primary_menu_items)) {        foreach ($primary_menu_items as $pm) {            if (in_array('external', $pm[3]) or                    (int) get_option('page_on_front') === $pm[0])                continue;            $page_query = new WP_Query(                    array('post_type' => 'page',                'post__in' => array($pm[0]),                'posts_per_page' => 1,                    ));            if ($page_query->have_posts()): while ($page_query->have_posts()):                    $page_query->the_post();                                        $addClass = $style = '';                                        if(has_post_thumbnail()){                        $bg = wp_get_attachment_image_src(get_post_thumbnail_id(),'full');                        $style = "style='background-image:url({$bg[0]});'";                                             }                    switch(get_the_ID()){                       // case (int)$smof_data['szia_about_page']:                          //  $addClass = 'szia_about';                          //  break;                        case (int)$smof_data['szia_services_page']:                           $addClass = 'szia_services';                           break;                        case (int)$smof_data['szia_clients_page']:                            $addClass = 'szia_clients';                            break;                        case (int)$smof_data['szia_work_page']:                            $addClass = 'szia_work';                            break;                        case (int)$smof_data['szia_contact_page']:                            $addClass = 'szia_contact';                            break;                        default:                            $addClass = 'container';                            break;                    }                    echo "<section id='{$pm[2]}' class='parallax_bg {$addClass}' {$style}>";                    the_content();                    echo '</section>';                endwhile;            endif;            wp_reset_query();        }    }else{        $page_query = new WP_Query(                array('post_type' => 'page',                        'posts_per_page' => -1,                ));        if ($page_query->have_posts()): while ($page_query->have_posts()):                $page_query->the_post();                $style = '';                if(has_post_thumbnail()){                    $bg = wp_get_attachment_image_src(get_post_thumbnail_id(),'full');                    $style = "style='background-image:url({$bg[0]});'";                                         }                $p = get_post(get_the_ID());                                echo "<section id='{$p->post_name}' class='parallax_bg' {$style}>";                the_content();                echo '</section>';            endwhile;        endif;        wp_reset_query();    }        ?></div><?phpget_footer();?>