<?php/* * Template Name: Frontpage Video *  */global $smof_data,$post;get_header();?><div id="content_wrapper">    <?php    $homeslug = 'home';    $frontpage = get_option('page_on_front');    if(!empty($frontpage)){        $home = get_page((int)$frontpage);                if(isset($home->post_name))            $homeslug = $home->post_name;    }    ?>        <div id="<?php echo $homeslug?>">        <?php        if(isset($smof_data['szia_home_parallax_slider']) &&                 !empty($smof_data['szia_home_parallax_slider'])):            $homebg = '';            if(has_post_thumbnail($post->ID)){                $attid = get_post_thumbnail_id($post->ID);                $bgimg = wp_get_attachment_image_src($attid);                $homebg = "style='background-image:url({$bgimg[0]})'";            }        ?>                <div id="home_video">            <canvas class="overlay"></canvas>            <!--Home Quotes-->            <div class="landing_text">                <ul class="home_quotes">                    <?php foreach($smof_data['szia_home_parallax_slider'] as $slide):?>                    <li>                        <h4><?php echo $slide['title']?></h4>                    </li>                    <?php endforeach;?>                </ul>                <?php echo isset($smof_data['szia_home_parallax_slider_add_text'])?                 $smof_data['szia_home_parallax_slider_add_text'] : '';?>            </div>            <?php            $aboutlink = '#';            if(isset($smof_data['szia_home_parallax_slider_next_section']))                $aboutlink .= $smof_data['szia_home_parallax_slider_next_section'];            ?>                        <div id="arrow_more"><a href="<?php echo $aboutlink?>"></a></div>        </div>        <?php        endif;        ?>    </div>    <?php    $primary_menu_items = szia_nav_menu_items();        if(!empty($primary_menu_items)){        foreach($primary_menu_items as $pm){                          if(in_array('external',$pm[3]) or                     (int)get_option('page_on_front') === $pm[0])                continue;                        $page_query = new WP_Query(                 array( 'post_type' => 'page',                 'post__in' => array($pm[0]),                 'posts_per_page' => 1,                 ) );            if($page_query->have_posts()): while($page_query->have_posts()):                $page_query->the_post();                $addClass = $style = '';                                if(has_post_thumbnail()){                    $bg = wp_get_attachment_image_src(get_post_thumbnail_id(),'full');                    $style = "style='background-image:url({$bg[0]});'";                                         }                                switch(get_the_ID()){                                        case (int)$smof_data['szia_about_page']:                        $addClass = 'szia_about';                        break;                    case (int)$smof_data['szia_services_page']:                        $addClass = 'szia_services';                        break;                    case (int)$smof_data['szia_clients_page']:                        $addClass = 'szia_clients';                        break;                    case (int)$smof_data['szia_work_page']:                        $addClass = 'szia_work';                        break;                    case (int)$smof_data['szia_contact_page']:                        $addClass = 'szia_contact';                        break;                    default:                        $addClass = 'container';                        break;                                    }                            echo "<section id='{$pm[2]}' class='parallax_bg {$addClass}' {$style}>";                            the_content();                                echo '</section>';                            endwhile; endif; wp_reset_query();                    }    }        ?></div><?phpget_footer();?>